        QHSM_DISPATCH(&a->super, e, a->prio);
        QF_gc(e);

        /* determine the next highest-priority AO ready to run... */
        QF_INT_DISABLE(); /* unconditionally disable interrupts */

        if (a->eQueue.frontEvt == (QEvt *)0) { /* empty queue? */
            QPSet_remove(&QF_readySet_, p);
        }

